class BYException(Exception):
    TYPE = 'STATUS_INVALID'


class BYMissingException(BYException):
    TYPE = 'YBY_MISSING'


class BYExpiredException(BYException):
    TYPE = 'STATUS_EXPIRED'


class BYRolesMismatchException(BYException):
    TYPE = 'STATUS_ROLES_MISMATCH'


class BYAppidMismatchException(BYException):
    TYPE = 'STATUS_APPID_MISMATCH'


class BYBadIPException(BYException):
    TYPE = 'STATUS_IP_MISMATCH'


class BYBadSignatureException(BYException):
    TYPE = 'STATUS_INVALID_SIGNATURE'


class BYFieldException(BYException):
    pass


class AuthorizationFailure(Exception):
    """Cannot authorize API client."""
    pass
